package com.example.demo.model.entities;

import lombok.Data;

import jakarta.persistence.*;

@Entity
@Table(name = "evaluation")
@Data
public class Evaluation {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Integer id;

    @ManyToOne
    @JoinColumn(name = "beneficiare_id", referencedColumnName = "id")
    private Utilisateur beneficiare;

    @ManyToOne
    @JoinColumn(name = "thematique_id", referencedColumnName = "id")
    private Thematique thematique;

    // Constructors, getters, setters, toString, equals, and hashCode will be automatically generated by Lombok
}

